mapper.update:

UPDATE PET_QNA SET QNA_NO=#{QnA_NO}, QNA_REF=#{QnA_Ref}, QNA_REPLY=#{QnA_Reply}, 
QNA_SUBJECT=#{QnA_Subject}, QNA_CONTENT=#{QnA_Content}, MEMBER_ID=#{member_ID}, 
QNA_IMG=#{QnA_IMG}, QNA_COUNT=#{QnA_Count}, QNA_DATE=#{QnA_Date}

mapper.select:
SELECT QNA_NO, QNA_REF, QNA_REPLY, QNA_SUBJECT, QNA_CONTENT, MEMBER_ID, QNA_IMG, QNA_COUNT, QNA_DATE FROM Pet_QnA
WHERE MEMBER_ID = #{member_ID}
ORDER BY QnA_Ref DESC, QnA_Reply
